	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = TramitePeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setEmpresaId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setRecepcionDelCredito($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setRecepcionDelTramite($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setPersonaEncargada($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setHorario($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setProcedimiento($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setDocumentacion($arr[$keys[6]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TramitePeer::DATABASE_NAME);

		if ($this->isColumnModified(TramitePeer::EMPRESA_ID)) $criteria->add(TramitePeer::EMPRESA_ID, $this->empresa_id);
		if ($this->isColumnModified(TramitePeer::RECEPCION_DEL_CREDITO)) $criteria->add(TramitePeer::RECEPCION_DEL_CREDITO, $this->recepcion_del_credito);
		if ($this->isColumnModified(TramitePeer::RECEPCION_DEL_TRAMITE)) $criteria->add(TramitePeer::RECEPCION_DEL_TRAMITE, $this->recepcion_del_tramite);
		if ($this->isColumnModified(TramitePeer::PERSONA_ENCARGADA)) $criteria->add(TramitePeer::PERSONA_ENCARGADA, $this->persona_encargada);
		if ($this->isColumnModified(TramitePeer::HORARIO)) $criteria->add(TramitePeer::HORARIO, $this->horario);
		if ($this->isColumnModified(TramitePeer::PROCEDIMIENTO)) $criteria->add(TramitePeer::PROCEDIMIENTO, $this->procedimiento);
		if ($this->isColumnModified(TramitePeer::DOCUMENTACION)) $criteria->add(TramitePeer::DOCUMENTACION, $this->documentacion);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(TramitePeer::DATABASE_NAME);

		$criteria->add(TramitePeer::EMPRESA_ID, $this->empresa_id);

		return $criteria;
	}

