		} // if ($deep)

			$this->aEmpresa = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseTramite:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseTramite::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


